sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	// Business Rule that places event on the queue when state set responded and resource is assigned, since info:'ATTACHMENTS' needed are on 'ServiceNow Resource Detail' table, and email template needs to be on table with the attachments

	//Grab Resource Manager email property value, will be used as event.parm2
	var resmanEmail = gs.getProperty('x_24859_ps.resource.manager.email');
	if (!resmanEmail) return;

	//Grab Resource Detail record for the assigned_to user
	var rs = new GlideRecord('x_24859_ps_servicenow_resource_detail');
	rs.addQuery('u_user_ref',current.assigned_to);
	rs.query();

	if (rs.hasNext() && rs.next()) {
		//Grabs Resource Detail gliderecord, pass to event to override 'triggered' by record
		//this allows notification to use 'resource detail' vs 'staffing request'
		//table values in templates
		//gs.eventQueue(
		// event name
		// gliderecord of record that fired event, this case we override - rs vs. current
		// event.parm1  -  we pass the staffing record id
		// event.parm2  -  we pass the resource manager email
		// )

		rs.get(rs.getValue('sys_id'));
		gs.eventQueue('x_24859_ps.respond_resman',rs,current.sys_id,resmanEmail);
	}
})(current, previous);
